import { useState } from "react";
import { useHistory } from "react-router-dom";
import styles from "../home.module.css";

function App() {
  const history = useHistory();

  const [ConsumerName, setConsumerName] = useState("");
  const [ConsumerAddress, setConsumerAddress] = useState("");
  const [ConsumerNumber, setConsumerNumber] = useState("");

  async function ChangeOwnerProduct(event) {
    event.preventDefault();

    const response = await fetch(
      "/channels/mychannel/chaincodes/smartContract",
      {
        method: "POST",
        authorization: {
          "Bearer-Token": window.token,
          Authorization: "Bearer " + window.token,
        },
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          fcn: "changeProductOwner",
          peers: ["peer0.org1.example.com", "peer1.org2.example.com"],
          chaincodeName: "smartContract",
          channelName: "mychannel",
          args: [ConsumerName, ConsumerAddress, ConsumerNumber],
        }),
      }
    );

    const data = await response.json();

    if (data.status === "ok") {
      alert("Prod owner Changed successfully");
    } else {
      console.log("ERROR");
    }
  }

  return (
    <div>
      <div class={styles.topnav}>
        <a href="/">Home</a>
        <a href="register">Register</a>
        <a href="login">Login</a>
        <a class={styles.active} href="changeowner">
          Change Owner
        </a>
        <a href="authenticate">Authenticate</a>
        <a href="addproduct">Add Product</a>
        <a href="addseller">Add Seller</a>
      </div>

      <h1>Change Product Owner</h1>
      <form onSubmit={ChangeOwnerProduct}>
        <input
          value={ConsumerName}
          onChange={(e) => setConsumerName(e.target.value)}
          type="text"
          placeholder="Item Name"
        />
        <br />
        <input
          value={ConsumerAddress}
          onChange={(e) => setConsumerAddress(e.target.value)}
          type="text"
          placeholder="Manufacturer"
        />
        <br />
        <input
          value={ConsumerNumber}
          onChange={(e) => setConsumerNumber(e.target.value)}
          type="text"
          placeholder="Consumer Number"
        />
        <br />
        <br />
        <input type="submit" value="Change Product Owner" />
      </form>
    </div>
  );
}

export default App;
