import { useState } from "react";
import { useHistory } from "react-router-dom";
import styles from "../home.module.css";

function App() {
  const history = useHistory();

  const [Item, setItem] = useState("");

  async function QueryAssetHistory(event) {
    event.preventDefault();

    fetch(
      'http://localhost:4000/channels/mychannel/chaincodes/smartContract?args=["' +
        Item +
        '"]&peer=peer0.org1.example.com&fcn=getHistoryForAsset',
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      }
    )
      .then(function (response) {
        return response.json();
      })
      .then(function (data) {
        alert("Asset history retreived successfully");
        console.log(data);

        let container = document.getElementById("container");

        let table = document.createElement("table");

        data.forEach((item) => {
          let tr = document.createElement("tr");

          let vals = Object.values(item);

          vals.forEach((elem) => {
            let td = document.createElement("td");
            if (typeof elem === "object") {
              let vals1 = Object.values(elem);
              vals1.forEach((child) => {
                let td1 = document.createElement("td");
                td1.innerText = child;
                tr.appendChild(td1);
              });
            } else {
              td.innerText = elem;
              tr.appendChild(td);
            }
          });
          table.appendChild(tr);
        });
        container.appendChild(table);
      })
      .catch((error) => console.error("Error:", error));
  }

  return (
    <div>
      <div class={styles.topnav}>
        <a class={styles.active} href="authenticate">Authenticate</a>
        <a class={styles.active} href="queryassethistory">Query Asset</a>
        <a href="/">Logout</a>
      </div>
      <h1>Query Asset History</h1>
      <form onSubmit={QueryAssetHistory}>
        <input
          value={Item}
          onChange={(e) => setItem(e.target.value)}
          type="text"
          placeholder="Item Number"
        />
        <br />

        <h4 id="prod"></h4>
        <input type="submit" value="Query Asset History" />
        <thead>
          <th>Transaction Id</th>
          <th>Product Name</th>
          <th>Brand</th>
          <th>Color</th>
          <th>Price</th>
          <th>Size</th>
          <th>Owner</th>
          <th>SellerId</th>
        </thead>
        <container id="container"></container>
      </form>
    </div>
  );
}

export default App;
