import { useState } from "react";
import { useHistory } from "react-router-dom";
import styles from "../home.module.css";

function App() {
  const history = useHistory();

  const [ProductIdAuth, setAuthProductId] = useState("");

  async function AuthenticateProduct(event) {
    event.preventDefault();

    const response = await fetch("http://localhost:4000/channels/mychannel/chaincodes/smartContract?args=[\""+ProductIdAuth+"\"]&peer=peer0.org1.example.com&fcn=authenticate", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("token"),
      },
    }).then(function (response) {
      return response.json();
    });
    const data = await response.json();

    if (data.status === "ok") {
      alert("Product is Original");
    } else {
      alert("Product is Fake");
    }
  }

  return (
    <div>
      <div class={styles.topnav}>
        <a href="/">Home</a>
        <a href="register">Register</a>
        <a href="login">Login</a>
        <a href="changeowner">Change Owner</a>
        <a class={styles.active} href="authenticate">
          Authenticate
        </a>
        <a href="addproduct">Add Product</a>
        <a href="addseller">Add Seller</a>
      </div>
      <h1>Authenticate Product</h1>
      <form onSubmit={AuthenticateProduct}>
        <input
          value={ProductIdAuth}
          onChange={(e) => setAuthProductId(e.target.value)}
          type="text"
          placeholder="Product"
        />
        <br />
        <input type="submit" value="Authenticate" />
      </form>
    </div>
  );
}

export default App;
