import { useState } from "react";
import { useHistory } from "react-router-dom";
import styles from "../home.module.css";

function App() {
  const history = useHistory();

  const [Item, setItem] = useState("");

  async function QueryAllProducts(event) {
    event.preventDefault();

    fetch(
      "http://localhost:4000/channels/mychannel/chaincodes/smartContract?args=[\"ITEM\"]&peer=peer0.org1.example.com&fcn=queryAllProducts",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      }
    )
      .then(function (response) {
        return response.json();
      })
      .then(function (data) {
        alert("Product query successful");
        console.log(data);

        let container = document.getElementById("container");

        let table = document.createElement("table");

        data.forEach((item) => {
          let tr = document.createElement("tr");

          let vals = Object.values(item);

          vals.forEach((elem) => {
            let td = document.createElement("td");
            if (typeof elem === "object") {
              let vals1 = Object.values(elem);
              vals1.forEach((child) => {
                let td1 = document.createElement("td");
                td1.innerText = child;
                tr.appendChild(td1);
              });
            } else {
              td.innerText = elem;
              tr.appendChild(td);
            }
          });
          table.appendChild(tr);
        });
        container.appendChild(table);
      })
      .catch((error) => console.error("Error:", error));
  }

  return (
    <div>
      <div class={styles.topnav}>
        <a href="/">Home</a>
        <a href="register">Register</a>
        <a href="login">Login</a>
        <a href="changeowner">Change Owner</a>
        <a href="authenticate">Authenticate</a>
        <a href="addproduct">Add Product</a>
        <a href="addseller">Add Seller</a>
        <a class={styles.active} href="queryall">
          Query All Products
        </a>
      </div>
      <h1>Query All Products</h1>
      <form onSubmit={QueryAllProducts}>
        <input
          value={Item}
          onChange={(e) => setItem(e.target.value)}
          type="text"
          placeholder="Item Number"
        />
        <br />

        <h4 id="prod"></h4>
        <input type="submit" value="Query All" />
        <container id="container"></container>
      </form>
    </div>
  );
}

export default App;
